import React, {useState, useContext, useEffect} from 'react';
import { AuthContext } from '../Context';
import {
	View,
	Text,
	TouchableOpacity,
	SafeAreaView,
	Image,
} from 'react-native';
import Modal from 'react-native-modal';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
// import Toggle from 'react-native-toggle-element';
import Colours from '../resources/themes/Colours';
import { useTheme } from '@react-navigation/native';

function ThemeCircle({ colorTheme, onPress, selected }) {
	return (
		<View>
			<TouchableOpacity
				style={{
					alignItems: 'center',
					justifyContent: 'center',
					flexDirection: 'row',
					height: 44,
					width: 44,
					borderRadius: 22,
					backgroundColor: selected
						? colorTheme.Selected
						: colorTheme.NotSelected,
				}}
				onPress={onPress}
			>
				{selected ? (
					<View
						style={{
							alignItems: 'center',
							flexDirection: 'row',
							height: 30,
							width: 30,
							borderRadius: 15,
							backgroundColor: colorTheme.NotSelected,
						}}
					/>
				) : (
					<></>
				)}
			</TouchableOpacity>
		</View>
	);
}

const Tab = ({ color, icon, onPress, title, isImage = false }) => (
	<TouchableOpacity
		style={{ alignItems: 'center', flexDirection: 'row', width: '100%' }}
		onPress={onPress}
	>
		{isImage ? (
			<Image
				source={icon}
				resizeMode='contain'
				style={{
					width: 40,
					height: 40,
					tintColor: color,
					// transform: [ { rotate: rotate }]
				}}
			/>
		) : (
			<MaterialCommunityIcons name={icon} color={color} size={40} />
		)}
		<Text
			style={{
				textAlign: 'center',
				color: color,
				fontSize: 20,
				fontWeight: 'bold',
			}}
		>
			{' '}
			{title}{' '}
		</Text>
	</TouchableOpacity>
);

function SideMenu(props) {
	const { changeColorTheme, getColor, changeModeTheme, getMode, getToken } =

		useContext(AuthContext);

	const { colors } = useTheme();
	const [color, setColor] = useState(getColor);
	const [userName, setUserName] = useState('');

	let defaultMode = true;
	if (getMode == 'dark') {
		defaultMode = false;
	}

	useEffect(() => {
		if (props.modalVisible) {
			setColor(getColor)
		}
	}, [props.modalVisible]);

	useEffect(() => {
		fetch('http://localhost:5000/api/v1.0.0/user/viewAccount', {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
				'authentication-token': getToken,
			},
		})
		.then((res) => res.json())
		.then((data) => {
			setUserName(data.userName);
		})
		.catch((err) => console.log(err));
	}, []);
	{/* TODO: Change this to a global variable */}


	const [toggleValue, setToggleValue] = useState(!defaultMode);

	function colorChange(color) {
		changeColorTheme(color);
		setColor(color);
	}

	return (
		<Modal
			swipeDirection='left'
			onSwipeComplete={(e) => {
				props.setModalVisible(false);
			}}
			transparent={true}
			backdropOpacity={0.2}
			onBackdropPress={() => props.setModalVisible(false)}
			style={{ margin: 0 }}
			isVisible={props.modalVisible}
			animationIn='slideInLeft'
			animationOut='slideOutLeft'
		>
			<View
				style={{
					height: '100%',
					width: '80%',
					paddingTop: '10%',
					paddingLeft: '5%',
					backgroundColor: colors.background,
				}}
			>
				<SafeAreaView>
					<View style={{ marginHorizontal: '6%', height: '100%' }}>
						<View
							style={{
								flexDirection: 'row',
								justifyContent: 'space-between',
								alignItems: 'center',
								marginBottom: '6%',
							}}
						>
							<View
								style={{ flexDirection: 'row', alignItems: 'center', flex: 1 }}
							>
								<TouchableOpacity style={{ justifyContent: 'center' }}>
									<View
										style={{
											backgroundColor: colors.Quaternary,
											width: 50,
											height: 50,
											borderRadius: 25,
										}}
									></View>
								</TouchableOpacity>
								<View
									style={{
										flexDirection: 'row',
										flex: 1,
										marginHorizontal: '4%',
									}}
								>
									<Text
										numberOfLines={1}
										style={{
											fontSize: 20,
											fontWeight: 'bold',
											color: colors.Quaternary,
											textAlign: 'center',
										}}
									>
										{ userName }
									</Text>
								</View>
							</View>
							<TouchableOpacity
								style={{ height: 40, justifyContent: 'center' }}
								onPress={() => {
									props.setModalVisible(false);
									props.navigation.navigate('SettingsScreen');
								}}
							>
								<MaterialCommunityIcons
									name='cog'
									color={colors.Quaternary}
									size={40}
								/>
							</TouchableOpacity>
						</View>
						<TouchableOpacity
							style={{ marginHorizontal: '6%', marginBottom: '6%' }}
						>
							<Text
								style={{
									fontSize: 18,
									color: colors.Quaternary,
									textAlign: 'center',
								}}
							>
								"What you choose to struggle in is what you’ll ultimately
								become.”
							</Text>
							<Text
								style={{
									fontSize: 18,
									color: colors.Quaternary,
									textAlign: 'right',
								}}
							>
								{' '}
								- Mark Manson{' '}
							</Text>
						</TouchableOpacity>
						<View
							style={{
								marginLeft: '6%',
								height: '50%',
								justifyContent: 'space-between',
								marginBottom: '12%',
							}}
						>
							<Tab
								color={colors.Quaternary}
								icon={'star'}
								title={'Achievements'}
								onPress={() => {
									props.setModalVisible(false);
									props.navigation.navigate('AchievementScreen');
								}}
							/>
							<Tab
								color={colors.Quaternary}
								icon={'account-circle'}
								title={'Account'}
								onPress={() => {
									props.setModalVisible(false);
									props.navigation.navigate('ProfileScreen');
								}}
							/>
							<Tab
								color={colors.Quaternary}
								icon={'bullhorn'}
								title={'Feedback'}
								onPress={() => {
									props.setModalVisible(false);
									props.navigation.navigate('FeedbackScreen');
								}}
							/>
							<Tab
								color={colors.Quaternary}
								icon={'bug'}
								title={'Report a Bug'}
								onPress={() => {
									props.setModalVisible(false);
									props.navigation.navigate('ReportABugScreen');
								}}
							/>
							<Tab
								color={colors.Quaternary}
								icon={'account-group'}
								title={'Collaborators'}
								onPress={() => {
									props.setModalVisible(false);
									props.navigation.navigate('CollaboratorsScreen');
								}}
							/>
							<Tab
								color={colors.Quaternary}
								icon={'hand-heart'}
								title={'Support Us!'}
								onPress={() => {
									props.setModalVisible(false);
									props.navigation.navigate('SupportUsScreen');
								}}
							/>
							<Tab
								color={colors.Quaternary}
								icon={require('../resources/images/Icon.png')}
								title={'About'}
								isImage={true}
								onPress={() => {
									props.setModalVisible(false);
									props.navigation.navigate('AboutScreen');
								}}
							/>
						</View>
						<View
							style={{
								flexDirection: 'row',
								justifyContent: 'space-between',
								marginHorizontal: '6%',
								marginBottom: '6%',
							}}
						>
							<ThemeCircle
								colorTheme={Colours.Green}
								selected={color == 'green'}
								onPress={() => colorChange('green')}
							/>
							<ThemeCircle
